local KingdomMapUtils = require('KingdomMapUtils')

---@class KingdomSelectionHighlight
---@field highlightColor : CS.Kingdom.TileHighlightColor
local KingdomSelectionHighlight = class("KingdomSelectionHighlight")

function KingdomSelectionHighlight:SetData(sizeX, sizeZ, color)
    local staticMapData = KingdomMapUtils.GetStaticMapData()
    local unitSizeX = sizeX * staticMapData.UnitsPerTileX
    local unitSizeZ = sizeZ * staticMapData.UnitsPerTileZ
    self.selectionHighlight.localPosition = CS.UnityEngine.Vector3(unitSizeX / 2, 0.01, unitSizeZ / 2) 
    self.selectionHighlight.localScale = CS.UnityEngine.Vector3(unitSizeX, 1, unitSizeZ)
    self.highlightColor:SetColor(color)
end

return KingdomSelectionHighlight